<?php	namespace Estimate\Model;		use Zend\Db\TableGateway\TableGateway;		class AnswerEstimateTable 	{		protected $tableGateway;				public function __construct(TableGateway $dbTable)		{			$this->tableGateway=$dbTable;		}				public function getAnswer($id)		{			$id=(int) $id;			$rowset=$this->tableGateway->select(array('ID_Answer'=>$id));			$row=$rowset->current();			if(!$row){				throw new \Exception("Tidak Menemukan Data yang dicari berdasarkan ID");			}			return $row;		}				public function decodeAnswer($data,$ind)		{			foreach($data as $key=>$value){				$dset=$value[$ind];			}			$retre=\Zend\Json\Json::decode($dset,true);			return $retre;		}				public function getDataset($id)		{			$data_re=$this->decodeAnswer($this->getAnswer($id),'answer_data');			return $data_re;		}			}		?>