<?php	namespace Estimate\Model;		use Zend\Db\TableGateway\TableGateway;		class QuestionEstimateTable 	{		protected $tableGateway;				public function __construct(TableGateway $dbTable)		{			$this->tableGateway=$dbTable;		}				public function getQuestion($id)		{			$id=(int) $id;			$rowset=$this->tableGateway->select(array('ID_Question'=>$id));			$row=$rowset->current();			if(!$row){				throw new \Exception("Tidak Menemukan Data yang dicari berdasarkan ID");			}			return $row;		}				public function getAbbreviation()		{			$rowset=$this->tableGateway->getSql()->select();			$row=$rowset->columns(array('abbreviation'));			$resultSet=$this->tableGateway->selectWith($row);			if(!$resultSet){				throw new \Exception("Tidak Menemukan Data yang dicari berdasarkan abbreviation");			}			$value=array();			$value[]="ID";			foreach($resultSet as $data){				foreach($data as $val){					foreach($val as $te){						$value[]=$te;					}				}			}			return $value;		}			}		?>